function stream_layers(a,b,c){function d(a){var c=1/(.1+Math.random()),d=2*Math.random()-.5,e=10/(.1+Math.random());for(var f=0;f<b;f++){var g=(f/b-d)*e;a[f]+=c*Math.exp(-g*g)}}return arguments.length<3&&(c=0),d3.range(a).map(function(){var a=[],e;for(e=0;e<b;e++)a[e]=c+c*Math.random();for(e=0;e<5;e++)d(a);return a.map(stream_index)})}function stream_waves(a,b){return d3.range(a).map(function(a){return d3.range(b).map(function(c){var d=20*c/b-a/3;return 2*d*Math.exp(-0.5*d)}).map(stream_index)})}function stream_index(a,b){return{x:b,y:Math.max(0,a)}}