function update(){vertices[0]=d3.mouse(this),svg.selectAll("path").data(d3.geom.voronoi(vertices).map(function(a){return"M"+a.join("L")+"Z"})).filter(function(a){return this.getAttribute("d")!=a}).attr("d",function(a){return a})}var w=960,h=500,vertices=d3.range(100).map(function(a){return[Math.random()*w,Math.random()*h]}),svg=d3.select("#chart").append("svg").attr("width",w).attr("height",h).attr("class","PiYG").on("mousemove",update);svg.selectAll("path").data(d3.geom.voronoi(vertices)).enter().append("path").attr("class",function(a,b){return b?"q"+b%9+"-9":null}).attr("d",function(a){return"M"+a.join("L")+"Z"}),svg.selectAll("circle").data(vertices.slice(1)).enter().append("circle").attr("transform",function(a){return"translate("+a+")"}).attr("r",2)