require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.hsl");suite.addBatch({hsl:{topic:function(){return d3.hsl},"does not clamp channel values":function(a){assert.hslEqual(a(-100,-1,-2),-100,-1,-2),assert.hslEqual(a(400,2,3),400,2,3)},"converts string channel values to numbers":function(a){assert.hslEqual(a("180",".5",".6"),180,.5,.6)},"converts null channel values to zero":function(a){assert.hslEqual(a(null,null,null),0,0,0)},"exposes h, s and l properties":function(a){var b=a("hsl(180, 50%, 60%)");assert.equal(b.h,180),assert.equal(b.s,.5),assert.equal(b.l,.6)},"changing h, s or l affects the string format":function(a){var b=a("hsl(180, 50%, 60%)");b.h++,b.s+=.1,b.l+=.1,assert.equal(b+"","#85dfe0")},'parses hexadecimal shorthand format (e.g., "#abc")':function(a){assert.hslEqual(a("#abc"),210,.25,.733333)},'parses hexadecimal format (e.g., "#abcdef")':function(a){assert.hslEqual(a("#abcdef"),210,.68,.803922)},'parses HSL format (e.g., "hsl(210, 64%, 13%)")':function(a){assert.hslEqual(a("hsl(210, 64.7058%, 13.33333%)"),210,.647058,.133333)},'parses color names (e.g., "moccasin")':function(a){assert.hslEqual(a("moccasin"),38.108108,1,.854902),assert.hslEqual(a("aliceblue"),208,1,.970588),assert.hslEqual(a("yellow"),60,1,.5)},'parses and converts RGB format (e.g., "rgb(102, 102, 0)")':function(a){assert.hslEqual(a("rgb(102, 102, 0)"),60,1,.2)},"can convert from RGB":function(a){assert.hslEqual(a(d3.rgb(12,34,56)),210,.647058,.133333)},"can convert from HSL":function(a){assert.hslEqual(a(d3.hsl(20,.8,.3)),20,.8,.3)},"can convert to RGB":function(a){assert.rgbEqual(a("steelblue").rgb(),70,130,180)},"can derive a brighter color":function(a){assert.hslEqual(a("steelblue").brighter(),207.272727,.44,.7002801),assert.hslEqual(a("steelblue").brighter(.5),207.272727,.44,.5858964),assert.hslEqual(a("steelblue").brighter(1),207.272727,.44,.7002801),assert.hslEqual(a("steelblue").brighter(2),207.272727,.44,1.0004002)},"can derive a darker color":function(a){assert.hslEqual(a("lightsteelblue").darker(),213.913043,.4107143,.5462745),assert.hslEqual(a("lightsteelblue").darker(.5),213.913043,.4107143,.6529229),assert.hslEqual(a("lightsteelblue").darker(1),213.913043,.4107143,.5462745),assert.hslEqual(a("lightsteelblue").darker(2),213.913043,.4107143,.38239216)},"string coercion returns RGB format":function(a){assert.strictEqual(a("hsl(60, 100%, 20%)")+"","#666600"),assert.strictEqual(a(d3.hsl(60,1,.2))+"","#666600")}}}),suite.export(module)