require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.round");suite.addBatch({round:{topic:function(){return d3.round},"returns a number":function(a){assert.isNumber(a(42))},"returns zero for zero":function(a){assert.equal(a(0),0)},"ignores degenerate input":function(a){assert.isNaN(a(NaN)),assert.equal(a(Infinity),Infinity),assert.equal(a(-Infinity),-Infinity)},"returns integers by default":function(a){assert.equal(a(10.6),11),assert.equal(a(10.4),10),assert.equal(a(.6),1),assert.equal(a(.4),0),assert.equal(a(-0.6),-1),assert.equal(a(-0.4),0),assert.equal(a(-10.6),-11),assert.equal(a(-10.4),-10)},"rounds to the specified decimal place":function(a){assert.inDelta(a(10.56,1),10.6,1e-6),assert.inDelta(a(10.54,1),10.5,1e-6),assert.inDelta(a(.56,1),.6,1e-6),assert.inDelta(a(.54,1),.5,1e-6),assert.inDelta(a(-0.56,1),-0.6,1e-6),assert.inDelta(a(-0.54,1),-0.5,1e-6),assert.inDelta(a(-10.56,1),-10.6,1e-6),assert.inDelta(a(-10.54,1),-10.5,1e-6),assert.inDelta(a(10.556,2),10.56,1e-6),assert.inDelta(a(10.554,2),10.55,1e-6),assert.inDelta(a(.556,2),.56,1e-6),assert.inDelta(a(.554,2),.55,1e-6),assert.inDelta(a(-0.556,2),-0.56,1e-6),assert.inDelta(a(-0.554,2),-0.55,1e-6),assert.inDelta(a(-10.556,2),-10.56,1e-6),assert.inDelta(a(-10.554,2),-10.55,1e-6)},"rounds to the specified significant digits":function(a){assert.equal(a(123.45,-1),120),assert.equal(a(345.67,-1),350),assert.equal(a(-123.45,-1),-120),assert.equal(a(-345.67,-1),-350),assert.equal(a(123.45,-2),100),assert.equal(a(456.78,-2),500),assert.equal(a(-123.45,-2),-100),assert.equal(a(-456.78,-2),-500),assert.equal(a(123.45,-3),0),assert.equal(a(567.89,-3),1e3),assert.equal(a(-123.45,-3),0),assert.equal(a(-567.89,-3),-1e3)}}}),suite.export(module)