require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("selection.empty");suite.addBatch({"select(body)":{topic:function(){return d3.select("body").html("")},"returns true for empty selections":function(a){assert.isTrue(a.select("foo").empty())},"returns false for non-empty selections":function(a){assert.isFalse(a.empty())},"ignores null nodes":function(a){var b=d3.select("body");b[0][0]=null,assert.isTrue(b.empty())}}}),suite.addBatch({"selectAll(div)":{topic:function(){var a=d3.select("body").html("");return a.append("div").append("span"),a.append("div"),a.selectAll("div")},"returns true for empty selections":function(a){assert.isTrue(a.select("foo").empty())},"returns false for non-empty selections":function(a){assert.isFalse(a.empty()),assert.isFalse(a.select("span").empty())},"ignores null nodes":function(a){var b=d3.selectAll("div");b[0][0]=null,assert.isTrue(b.select("span").empty())}}}),suite.export(module)