require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("selection.node");suite.addBatch({"select(body)":{topic:function(){return d3.select("body").html("")},"returns null for empty selections":function(a){assert.isNull(a.select("foo").node())},"returns the first element for non-empty selections":function(a){assert.isTrue(a.node()===document.body)},"ignores null nodes":function(a){var b=d3.select("body");b[0][0]=null,assert.isNull(b.node())}}}),suite.addBatch({"selectAll(div)":{topic:function(){var a=d3.select("body").html("");return a.append("div").append("span"),a.append("div"),a.selectAll("div")},"returns null for empty selections":function(a){assert.isNull(a.select("foo").node())},"returns the first element for non-empty selections":function(a){assert.isTrue(a.node()===a[0][0])},"ignores null nodes":function(a){var b=d3.selectAll("div");b[0][0]=null,assert.isTrue(b.node()===a[0][1])}}}),suite.export(module)