require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("selection.style");suite.addBatch({"select(body)":{topic:function(){return d3.select("body").html("")},"sets a property as a string":function(a){a.style("background-color","red"),assert.equal(document.body.style["background-color"],"red")},"sets a property as a number":function(a){a.style("opacity",.3),assert.equal(document.body.style.opacity,"0.3")},"sets a property as a function":function(a){a.style("background-color",function(){return"orange"}),assert.equal(document.body.style["background-color"],"orange")},"gets a property value":function(a){document.body.style.setProperty("background-color","yellow",""),assert.equal(a.style("background-color"),"yellow")},"observes the specified priority":function(a){a.style("background-color","green","important"),assert.equal(document.body.style.getPropertyPriority("background-color"),"important")},"removes a property as null":function(a){a.style("background-color","green").style("background-color",null),assert.equal(a.style("background-color"),"")},"removes a property as a function":function(a){a.style("background-color","green").style("background-color",function(){return null}),assert.equal(a.style("background-color"),"")},"returns the current selection":function(a){assert.isTrue(a.style("background-color","green")===a)}}}),suite.addBatch({"selectAll(div)":{topic:function(){return d3.select("body").html("").selectAll("div").data(d3.range(2)).enter().append("div")},"sets a property as a string":function(a){a.style("background-color","red"),assert.equal(a[0][0].style["background-color"],"red"),assert.equal(a[0][1].style["background-color"],"red")},"sets a property as a number":function(a){a.style("opacity",.5),assert.equal(a[0][0].style.opacity,"0.5"),assert.equal(a[0][1].style.opacity,"0.5")},"sets a property as a function":function(a){a.style("background-color",d3.interpolateRgb("orange","yellow")),assert.equal(a[0][0].style["background-color"],"#ffa500"),assert.equal(a[0][1].style["background-color"],"#ffff00")},"gets a property value":function(a){a[0][0].style.setProperty("background-color","green",""),assert.equal(a.style("background-color"),"green")},"observes the specified priority":function(a){a.style("background-color","blue","important"),assert.equal(a[0][0].style.getPropertyPriority("background-color"),"important"),assert.equal(a[0][1].style.getPropertyPriority("background-color"),"important")},"removes a property as null":function(a){a.style("background-color","red").style("background-color",null),assert.equal(a[0][0].style["background-color"],""),assert.equal(a[0][1].style["background-color"],"")},"removes a property as a function":function(a){a.style("background-color","red").style("background-color",function(){return null}),assert.equal(a[0][0].style["background-color"],""),assert.equal(a[0][1].style["background-color"],"")},"ignores null nodes":function(a){var b=d3.selectAll("div");b[0][1]=null,b.style("background-color",null).style("background-color","red"),assert.equal(a[0][0].style["background-color"],"red"),assert.equal(a[0][1].style["background-color"],"")},"returns the current selection":function(a){assert.isTrue(a.style("background-color","green")===a)}}}),suite.export(module)