require("../env");var assert=require("assert");module.exports={topic:function(){return d3.select("body").html("").selectAll().data(["foo","bar"]).enter().append("div").attr("class",String)},"defaults to zero":function(a){var b=a.transition();assert.strictEqual(b[0][0].delay,0),assert.strictEqual(b[0][1].delay,0)},"can specify delay as a number":function(a){var b=a.transition().delay(150);assert.strictEqual(b[0][0].delay,150),assert.strictEqual(b[0][1].delay,150),b.delay(250),assert.strictEqual(b[0][0].delay,250),assert.strictEqual(b[0][1].delay,250)},"can specify delay as a negative number":function(a){var b=a.transition().delay(-250);assert.strictEqual(b[0][0].delay,-250),assert.strictEqual(b[0][1].delay,-250)},"NaN delays are treated as 0ms":function(a){var b=a.transition().delay(NaN);assert.strictEqual(b[0][0].delay,0),assert.strictEqual(b[0][1].delay,0)},"floating-point durations are floored to integers":function(a){var b=a.transition().delay(14.6);assert.strictEqual(b[0][0].delay,14),assert.strictEqual(b[0][1].delay,14);var b=a.transition().delay("16.99");assert.strictEqual(b[0][0].delay,16),assert.strictEqual(b[0][1].delay,16)},"can specify delay as a function":function(a){function f(a,e){return b.push(a),c.push(e),d.push(this),e*20}var b=[],c=[],d=[],e=a.transition().delay(f);assert.strictEqual(e[0][0].delay,0),assert.strictEqual(e[0][1].delay,20),assert.deepEqual(b,["foo","bar"],"expected data, got {actual}"),assert.deepEqual(c,[0,1],"expected index, got {actual}"),assert.domEqual(d[0],e[0][0].node,"expected this, got {actual}"),assert.domEqual(d[1],e[0][1].node,"expected this, got {actual}")},"coerces delay to a number":function(a){var b=a.transition().delay("150");assert.strictEqual(b[0][0].delay,150),assert.strictEqual(b[0][1].delay,150)}}