require("../env");var assert=require("assert");module.exports={topic:function(){var a=d3.select("body").append("div").selectAll("div").data(["one","two","three","four"]).enter().append("div").attr("class",String);return a.filter(function(a,b){return b>0}).append("span"),a[0][3]=null,a.transition().delay(function(a,b){return b*13}).duration(function(a,b){return b*21})},"selects the first matching element":function(a){var b=a.select("span");assert.domEqual(b[0][1].node.parentNode,a[0][1].node),assert.domEqual(b[0][2].node.parentNode,a[0][2].node)},"ignores null elements":function(a){var b=a.select("span");assert.isNull(b[0][3])},"propagates data to the selected elements":function(a){var b=a.select("span");assert.equal(b[0][1].node.__data__,"two"),assert.equal(b[0][2].node.__data__,"three")},"propagates delay to the selected elements":function(a){var b=a.select("span");assert.equal(b[0][1].delay,13),assert.equal(b[0][2].delay,26)},"propagates duration to the selected elements":function(a){var b=a.select("span");assert.equal(b[0][1].duration,21),assert.equal(b[0][2].duration,42)},"does not propagate data if no data was specified":function(a){delete a[0][1].node.__data__,delete a[0][1].node.firstChild.__data__;var b=a.select("span");assert.isUndefined(b[0][1].node.__data__),assert.equal(b[0][2].node.__data__,"three")},"returns null if no match is found":function(a){var b=a.select("span");assert.isNull(b[0][0])},"inherits transition id":function(a){var b=a.id,c=a.select("span"),d=a.select("span");assert.equal(c.id,b),assert.equal(d.id,b)}}