require("../env");var assert=require("assert");module.exports={topic:function(){var a=d3.select("body").append("div").selectAll("div").data(["one","two","three","four"]).enter().append("div").attr("class",String);return a.filter(function(a,b){return b>0}).append("span"),a.filter(function(a,b){return b>1}).append("span"),a[0][3]=null,a.transition().delay(function(a,b){return b*13}).duration(function(a,b){return b*21})},"selects all matching elements":function(a){var b=a.selectAll("span");assert.domEqual(b[1][0].node.parentNode,a[0][1].node),assert.domEqual(b[2][0].node.parentNode,a[0][2].node),assert.domEqual(b[2][1].node.parentNode,a[0][2].node)},"ignores null elements":function(a){var b=a.selectAll("span");assert.equal(b.length,3)},"propagates delay to the selected elements":function(a){var b=a.selectAll("span");assert.domEqual(b[1][0].delay,13),assert.domEqual(b[2][0].delay,26),assert.domEqual(b[2][1].delay,26)},"propagates duration to the selected elements":function(a){var b=a.selectAll("span");assert.domEqual(b[1][0].duration,21),assert.domEqual(b[2][0].duration,42),assert.domEqual(b[2][1].duration,42)},"returns empty if no match is found":function(a){var b=a.selectAll("span");assert.isEmpty(b[0])},"inherits transition id":function(a){var b=a.id,c=a.selectAll("span"),d=a.selectAll("span");assert.equal(c.id,b),assert.equal(d.id,b)},"groups are not instances of NodeList":function(a){var b=a.selectAll(function(){return this.getElementsByClassName("span")});assert.isFalse(b[0]instanceof window.NodeList)}}