require("../env");var assert=require("assert");module.exports={topic:function(){return d3.select("body").append("div").transition()},"is approximately equal to now":function(a){var b=a.time;assert.inDelta(b,Date.now(),20)},"increases monotonically across transitions":function(a){var b=Date.now,c=Date.now();try{Date.now=function(){return++c};var d=d3.select("body").append("div").transition(),e=d3.select("body").append("div").transition();assert.isTrue(e.time>d.time)}finally{Date.now=b}},"is inherited by subtransitions":function(a){var b=Date.now,c=Date.now();try{Date.now=function(){return++c};var d=d3.select("body").append("div").transition(),e=d.transition();assert.equal(e.time,d.time)}finally{Date.now=b}}}