require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.values");suite.addBatch({values:{topic:function(){return d3.values},"enumerates every value":function(a){assert.deepEqual(a({a:1,b:2}),[1,2])},"includes values defined on prototypes":function(a){function b(){this.a=1,this.b=2}b.prototype.c=3,assert.deepEqual(a(new b),[1,2,3])},"includes null or undefined values":function(a){var b=a({a:undefined,b:null,c:NaN});assert.isUndefined(b[0]),assert.isNull(b[1]),assert.isNaN(b[2]),assert.equal(b.length,3)}}}),suite.export(module)