require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.csv.format");suite.addBatch({format:{topic:function(){return d3.csv.format},"takes an array of arrays as input":function(a){assert.equal(a([["a","b","c"],["1","2","3"]]),"a,b,c\n1,2,3")},"separates lines using unix newline":function(a){assert.equal(a([[],[]]),"\n")},"does not strip whitespace":function(a){assert.equal(a([["a "," b","c"],["1","2","3 "]]),"a , b,c\n1,2,3 ")},"does not quote simple values":function(a){assert.equal(a([["a"],[1]]),"a\n1")},"escapes double quotes":function(a){assert.equal(a([['"fish"']]),'"""fish"""')},"escapes unix newlines":function(a){assert.equal(a([["new\nline"]]),'"new\nline"')},"escapes commas":function(a){assert.equal(a([["oxford,comma"]]),'"oxford,comma"')}}}),suite.export(module)