require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.geo.equirectangular");suite.addBatch({equirectangular:{topic:function(){return d3.geo.equirectangular()},scale:{"defaults to 500":function(a){assert.equal(a.scale(),500)},"is coerced to a number":function(a){assert.strictEqual(a.scale("400"),a),assert.strictEqual(a.scale(),400),a.scale(500)}},translate:{"defaults to [480, 250]":function(a){assert.deepEqual(a.translate(),[480,250])},"is coerced to two numbers":function(a){assert.strictEqual(a.translate(["23","141"]),a),assert.strictEqual(a.translate()[0],23),assert.strictEqual(a.translate()[1],141),a.translate([480,250])}},"of San Francisco, CA":{"is at location [-122.446, 37.767]":function(a){assert.inDelta(a.invert([310,198]),[-122.446,37.767],.5)},"is at point [310, 198]":function(a){assert.inDelta(a([-122.446,37.767]),[310,198],.5)}}}}),suite.export(module)