require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.geo.mercator");suite.addBatch({mercator:{topic:function(){return d3.geo.mercator().translate([0,0]).scale(100)},Arctic:function(a){var b=a([0,85]);assert.inDelta(b[0],0,1e-6),assert.inDelta(b[1],-49.8362085,1e-6);var c=a.invert(b);assert.inDelta(c[0],0,1e-6),assert.inDelta(c[1],85,1e-6)},Antarctic:function(a){var b=a([0,-85]);assert.inDelta(b[0],0,1e-6),assert.inDelta(b[1],49.8362085,1e-6);var c=a.invert(b);assert.inDelta(c[0],0,1e-6),assert.inDelta(c[1],-85,1e-6)},Hawaii:function(a){var b=a([-180,0]);assert.inDelta(b[0],-50,1e-6),assert.inDelta(b[1],0,1e-6);var c=a.invert(b);assert.inDelta(c[0],-180,1e-6),assert.inDelta(c[1],0,1e-6)},Phillipines:function(a){var b=a([180,0]);assert.inDelta(b[0],50,1e-6),assert.inDelta(b[1],0,1e-6);var c=a.invert(b);assert.inDelta(c[0],180,1e-6),assert.inDelta(c[1],0,1e-6)},"Inversion works for non-zero translation":function(){var a=d3.geo.mercator().translate([123,99]).scale(100),b=a([0,85]),c=a.invert(b);assert.inDelta(c[0],0,1e-6),assert.inDelta(c[1],85,1e-6)}}}),suite.export(module)