function layout(a){return{value:a.value,depth:a.depth,x:a.x,y:a.y}}require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.layout.cluster");suite.addBatch({cluster:{topic:d3.layout.cluster,"can handle an empty children array":function(a){assert.deepEqual(a.nodes({value:1,children:[{value:1,children:[]},{value:1}]}).map(layout),[{value:1,depth:0,x:.5,y:0},{value:1,depth:1,x:.25,y:1},{value:1,depth:1,x:.75,y:1}])},"can handle zero-valued nodes":function(a){assert.deepEqual(a.nodes({value:0,children:[{value:0},{value:1}]}).map(layout),[{value:0,depth:0,x:.5,y:0},{value:0,depth:1,x:.25,y:1},{value:1,depth:1,x:.75,y:1}])},"can handle a single node":function(a){assert.deepEqual(a.nodes({value:0}).map(layout),[{value:0,depth:0,x:.5,y:0}])}}}),suite.export(module)